mapper.select:
SELECT 

FROM cms_succ_case a

WHERE a.id = #{id}

SELECT 

FROM cms_succ_case a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cms_succ_case a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_succ_case SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
