mapper.select:
SELECT 

FROM cms_recruitment a

WHERE a.id = #{id}

SELECT 

FROM cms_recruitment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cms_recruitment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
