mapper.select:
SELECT 

FROM cms_product a

WHERE a.id = #{id}

SELECT 

FROM cms_product a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.kd_id = #{cmsProductKind.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.pd_order ASC

SELECT 

FROM cms_product a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.pd_order ASC

mapper.update:
UPDATE cms_product SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE kd_id = #{cmsProductKind.id}
